// import logo from './logo.svg';
import './App.css';
import React, {Component} from 'react';

class App extends Component {

  constructor() {
    super();
    this.state = {
      monster : [
        {
          name: 'Zombie',
          id: 'mon1'
        },
        {
          name: 'Dracula',
          id: 'mon2'
        },
        {
          name: 'Frenkeinstein',
          id: 'mon3'
        }
      ]
    }

  }


  render(){
    return (
      <div className="App">
        { this.state.monster.map(monster => <h1 key={monster.name}>{monster.name}</h1>) }
      </div>
    );
  }
}

export default App;
